});

const workerAdded = (msg) =>({
    type:types.ADD_WORKER,
    payload:msg
});

export  const loadWorkers = ()=>{
    return function(dispatch){
       axios.get(`${API}/workers`)
       .then((resp) => dispatch(getWorkers(resp.data)))
       .catch(err => console.log(err));
    };
};
