import React, { useEffect, useState ,  } from "react";
import {
  Table,
  Container,
  Row,
  Col,
  Button,
  ButtonGroup,
  Form,
} from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { toast } from "react-toastify";
import {
  addVehicle,
  deleteVehicle,
  loadSingleVehicle,
  loadVehicles,
  updateVehicle,
} from "../redux/vehicles/actionsV";

const initialStateV = {
  type: "",
  model: "",
  year: ""
};

const Vehicles = () => {
  const [state, setState] = useState(initialStateV);
  const [editMode, setEditMode] = useState(false);
  const [vehicleId, setVehicalId] = useState(null);
  const dispatch = useDispatch();
  const { vehicles, msg, vehicle } = useSelector((state) => state.datav);

  const { type, model, year } = state;

  useEffect(() => {
    dispatch(loadVehicles());
  }, []);

  useEffect(() => {
    if (msg) {
      toast.success(msg);
    }
  }, [msg]);

  useEffect(() => {
    if (vehicle) {
      setState({ ...vehicle });
    }
  }, [vehicle]);

  const handleChange = (e) => {
    let { name, value } = e.target;
    setState({ ...state, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!type || !model || !year ) {
      toast.error("Please fill all input field");
    } else {
      if (!editMode) {
        dispatch(addVehicle(state));
        setState({ type: "", model: "", year: ""});
      } else {
        dispatch(updateVehicle(state, vehicleId));
        setState({ type: "", model: "", year: "" });
        setEditMode(false);
        setVehicalId(null);
      }
    }
  };

  const handleDelete = (id) => {
    if (window.confirm("Are you sure that you wanted to delete that vehicle ?")) {
      dispatch(deleteVehicle(id));
    }
  };

  const handleUpdate = (id) => {
    dispatch(loadSingleVehicle(id));
    setVehicalId(id);
    setEditMode(true);
  };
  return (
    <>
      
      <Container style={{ marginTop: "70px" }}>
        <Row>
          <Col md={4}>
            <Form onSubmit={handleSubmit}>
              <Form.Group>
                <Form.Label>Type</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Type"
                  name="type"
                  value={type || ""}
                  onChange={handleChange}
                />
              </Form.Group>
              <Form.Group>
                <Form.Label>Model</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Model"
                  name="model"
                  value={model || ""}
                  onChange={handleChange}
                />
              </Form.Group>
              <Form.Group>
                <Form.Label>Year</Form.Label>
                <Form.Control
                  type="number"
                  placeholder="Year"
                  name="year"
                  value={year || ""}
                  onChange={handleChange}
                />
              </Form.Group>
              <div className="d-grid gap-2 mt-2">
                <Button type="submit" variant="primary" size="lg">
                  {editMode ? "Update" : "Submit"}
                </Button>
              </div>
            </Form>
          </Col>
          <Col md={5}>
            <Table bordered hover>
              <thead>
                <tr>
                  <th>No.</th>
                  <th>Type</th>
                  <th>Model</th>
                  <th>Year</th>
                  <th>Action</th>
                </tr>
              </thead>
              {vehicles &&
                vehicles.map((item, index) => (
                  <tbody key={index}>
                    <tr>
                      <td>{index + 1}</td>
                      <td>{item.type}</td>
                      <td>{item.model}</td>
                      <td>{item.year}</td>
                      <td>
                        <ButtonGroup>
                          <Button
                            style={{ marginRight: "5px" }}
                            variant="danger"
                            onClick={() => handleDelete(item._id)}
                          >
                            Delete
                          </Button>
                          <Button
                            variant="success"
                            onClick={() => handleUpdate(item._id)}
                          >
                            Update
                          </Button>
                        </ButtonGroup>
                      </td>
                    </tr>
                  </tbody>
                ))}
            </Table>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default Vehicles;
