//       sessionStorage.setItem("token", JSON.stringify(resp.data.token));
//       console.log(resp.data);
//       const loggedInUser = sessionStorage.getItem("user");
//       console.log(loggedInUser);
//     })
//     .catch((err) => rest());
// }

export default function Login({ setToken }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [fName, setfName] = useState("");

  // useEffect(() => {
  //   const loggedInUser = sessionStorage.getItem("user");
  //   if (loggedInUser) {
  //     const foundUser = JSON.parse(loggedInUser);
  //     setUser(foundUser);
  //     setToken(foundUser.token);
  //     setfName(foundUser.fName);
  //   }
  // }, []);
  // const rest = () => {
  //   document.getElementById("email").value = "";
  //   document.getElementById("password").value = "";
  //   alert("Wrong email or Wrong passwod");
  // };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = await loginUser({
      email,
      password,
    });
    setToken(token);
  };
  // if there's a user show the message below

  if (user) {
  }

  return (
    <>
      <Router>
        <Switch>
          <Route path="/forgetmypass" exact component={Forgetmypass}></Route>
        </Switch>
      </Router>
      <div className="container">
        <div className="row">
          <div className="col-md-6 offset-md-3">
            <div className="card my-5">
              <form
                className="card-body cardbody-color p-lg-5"
                onSubmit={handleSubmit}
              >
                <div className="text-center">
                  <img
                    src={logo}
                    className="img-fluid profile-image-pic img-thumbnail rounded-circle my-3"
                    width="200px"
                    alt="profile"
                  />
                </div>
                <div className="mb-3">
                  <input
                    type="email"
                    className="form-control"
                    id="email"
                    aria-describedby="emailHelp"
                    placeholder="Email"
                    onChange={({ target }) => setEmail(target.value)}
                  />
                </div>
                <div className="mb-3">
                  <input
                    type="password"
                    className="form-control"
                    id="password"
                    placeholder="password"
                    onChange={({ target }) => setPassword(target.value)}
                  />
                </div>
                <div className="text-center">
                  <button
                    type="submit"
                    className="btn btn-color px-5 mb-5 w-100"
                  >
                    Login
                  </button>
                </div>
                <div
                  id="emailHelp"
                  className="form-text text-center mb-5 text-dark"
                ></div>

                <Button variant="info" style={{ marginLeft: "5px" }}>
                  Forget my password
                </Button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
Login.propTypes = {
  setToken: PropTypes.func.isRequired,
};
