import * as types from "./actionTypes";

const initialState = {
    workers:[],
    worker:{},
    msg: ""

}

const userReducer = (state = initialState,action) => {
    switch (action.type) {
        case types.GET_WORKERS:
            return{
                ...state,
                workers: action.payload
            };
        case types.ADD_WORKER:
            return{
                ...state,
                msg: action.payload
            }
        default:
            return state ;
    }
}

export default userReducer;
