import React , {useEffect, useState} from 'react'
import {Table  ,Row ,Container , Col ,Button ,ButtonGroup ,Form, Navbar} from "react-bootstrap"
import {useDispatch, useSelector} from 'react-redux';
import { addWorker, loadWorkers } from '../redux/actions';
import 'bootstrap/dist/css/bootstrap.min.css';
import { toast } from 'react-toastify';


const initialState={
    name : "",
    email:"",
    phoneNumber:"",
    age:""
}
const Workers= ()=>{
    const [state,setState]= useState(initialState);
    const dispatch = useDispatch();
    const {workers , msg} = useSelector(state => state.data);
    const {name ,email ,phoneNumber ,age} = state;
 useEffect(() => {
 dispatch(loadWorkers());
 },[]);

 useEffect(() => {
  if(msg){
      toast.success(msg)
  }
 },[msg])
const handleSumbit = (e) => {
    e.preventDefault();
    dispatch(addWorker(state));
};
const handleChange = (e) => {
 let {name,value} = e.target;
 setState({...state, [name]:value});
};
 return (
    <>
    <Container style={{marginTop : "70px"}}>
        <Row>
            <Col md ={4}>
                <Form onSubmit={handleSumbit}>
                    <Form.Group>
                        <Form.Label>Name </Form.Label>
                        <Form.Control
                        type="text"
                        placeholder='Name'
                        name="name"
                        value={name}
                        onChange={handleChange}
                        />
                        
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Email </Form.Label>
                        <Form.Control
                        type="email"
                        placeholder='Email'
                        name="email"
                        value={email}
                        onChange={handleChange}
                        />
                        
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Phone Number </Form.Label>
                        <Form.Control
                        type="number"
                        placeholder='Phone Number'
                        name="phoneNumber"
                        value={phoneNumber}
                        onChange={handleChange}
                        />
                        
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Age</Form.Label>
                        <Form.Control
                        type="date"
                        placeholder='Age'
                        name="age"
                        value={age}
                        onChange={handleChange}
                        />
                        
                    </Form.Group>
                    <div className='d-grid gap-2 mt-2'>
                        <Button type ="submit" variant="primary" size="lg">
                            Submit
                        </Button>

                    </div>
 
                </Form>
            </Col>
            <Col md ={8}>
             <Table boarded hover>
                 <thead>
                     <tr>
                     <th>No.</th>
                     <th>Name</th>
                     <th>Email</th>
                     <th>PhoneNumber</th>
                     <th>Age</th>
                     </tr>
                 </thead>
                 {workers && workers.map((item,index)=>(
                     <tbody key ={index}>
                         <tr>
                             
                             <td>
                                 <ButtonGroup>
                                     <Button style ={{marginRight:"5px"}} variant="danger">
                                         Delete
                                     </Button>
                                     <Button  variant="secondary">
                                         Update
                                     </Button>
                                 </ButtonGroup>
                             </td>

                         </tr>

                     </tbody>
                 ))}

             </Table>
            </Col>
        </Row>
    </Container>
    </>
 );
};

export default Workers
